mapper.select:
SELECT t1.*,t4.tol FROM (
	SELECT id, name, share, code,noEnd, amm, noDay, noHour,deltype,addtype
		FROM Share_ths_ai_detail t
		WHERE  t.noDay= #{noDay}

				and  t.noHour =#{noHour}


				and deltype=-1


				and code=#{code}


				and addtype=1

	) t1 LEFT JOIN (
			SELECT name,sum(succ) AS succ FROM Share_ths_ai_succ GROUP BY name
	) t2 ON t1.name = t2.name
	LEFT JOIN
		(SELECT name , share,code,count(1) AS tol FROM Share_ths_ai_detail t3 GROUP BY name , share,code) t4
		ON t1.name = t4.name AND t1.share=t4.share AND t4.code = t4.code
ORDER BY t2.succ DESC, t4.tol DESC,t1.addtype ASC,t1.deltype desc

SELECT *
FROM Share_ths_ai_detail t
WHERE  t.code = #{code}

	noDay >= #{noDay}

order by noDay, noHour

SELECT code,day,value,id FROM (
	SELECT code,day,en as value FROM Share_ths_ai_his WHERE code = #{code}

		day >= #{day}

	) t1 LEFT JOIN (
	SELECT noDay,count(1) AS id FROM Share_ths_ai_detail t2 WHERE code = #{code} GROUP BY noDay) t3
ON t1.day = t3.noDay ORDER BY t1.day
